//Inverted Pyramid Star Pattern#include <stdio.h>int main(){    int a , b;    for (a=1 ; a<=5 ; a++)    {        for (b =1 ; b < a ; b++)        {            printf (" ");        }        for (b=1 ; b <= 11-2*a ; b++)        {            printf ("*");                    }        printf ("\n");    }    return 0;}