//Mirrored Half Diamond Star Pattern#include <stdio.h>int main(){    int a, b, c=1, d=4;    for(a=1; a<10; a++)    {        for(b=1; b<=d; b++)            printf(" ");                for(b=1; b<=c; b++)            printf("*");                printf("\n");                if(a < 5)         {            c++;            d--;        }        else        {            c--;            d++;        }    }    return 0;}