//WAP to check whether a number is divisible by 5 and 11 or not#include <stdio.h>int main(){    int a;    printf("Enter a number:\n");    scanf ("%d" , &a);        if (a%5==0 && a%11==0)    { printf ("Number is divisible by 5 and 11");    }        else if(a%5==0 && a%11!=0)    { printf ("Number is divisible by 5 but not 11");    }        else if (a%5!=0 && a%11==0)    { printf ("Number is divisible by 11 but not 5");    }        else    {        printf ("Number is not divisible by 5 and 11");    }    return 0;}