//WAP to check whether triangle is equilateral, isosceles, or scalene#include <stdio.h>int main() {    int angle1 , angle2 , angle3;    printf ("Enter angles of a triangle:\n");    scanf ("%d%d%d" , &angle1,&angle2,&angle3);        if (angle1 ==angle2 && angle1 == angle3 )    {        printf ("It is an equilateral triangle");    }     else  if (angle1 ==angle2 || angle1 == angle3 || angle2 == angle3 )    {        printf ("It is an isosceles triangle");    }    else     {        printf ("It is a scalene traingle");    }    return 0;}