//WAP to convert binary to octal , hexa decimal and decimal number#include <stdio.h>#include <math.h>int main(){    int binary, octal , decimal=0 , rem , i=0, spare, decimal2 , hexadecimal = 0 ;    printf("Enter a binary number:\n");    scanf ("%d" , &binary);        while (binary !=0)    {        rem = binary %10;        spare = rem * pow (2 , i);        decimal += spare;        binary = binary / 10;        i++;    }    printf ("Decimal representation of the number is: %d \n" , decimal);    rem = 0;    i = 0;    decimal2 = decimal;    while (decimal != 0)    {         rem = (decimal%8) * pow (10 , i);        octal += rem;        decimal = decimal /8;        i++;    }    printf ("Octal representaion of the number is: %d \n" , octal );        rem = 0;    i = 0;    while (decimal2 != 0)    {         rem = (decimal2 % 16) * pow (10 , i);        hexadecimal += rem;        decimal2 = decimal2 /16;        i++;    }    printf ("Hexadecimal representation of the number is: %d" , hexadecimal );    return 0;}