//WAP to find frequency of each digit in a number#include <stdio.h>int main(){    int a, lastdigit, b;    int frequency [10];    printf("Enter number whose frequency u want to know:\n");    scanf ("%d" , &a);    for(b=0; b<10; b++)    {        frequency[b] = 0;    }    while (a != 0)    {        lastdigit = a%10;        frequency[lastdigit]++ ;         a = a/10;    }    for(b=0; b<10; b++)    {        printf("Frequency of %d = %d\n", b, frequency[b]);    }    return 0;}