//WAP to calculate profit or loss#include <stdio.h>int main() {    int a , b;    printf ("Enter the cost of product:\n");    scanf ("%d" , &a);    printf ("Enter the selliing price of product:\n");    scanf ("%d" , &b);        if (b>a)    {        printf ("Profit of Rs. %d" , b-a);    }    else if (a > b)    {        printf ("Loss of Rs. %d" , a-b);    }    else     {        printf ("Neither profit nor loss");    }    return 0;}