//WAP to find two's complement of a binary number#include <stdio.h>int main(){    int a , b=1;    char binary[10], onesCompliment[10] , twosCompliment[10];        printf("Enter binary value:\n");        gets(binary);    for(a=0; a<10; a++)    {        if(binary[a] == '1')        {            onesCompliment[a] = '0';        }        else if(binary[a] == '0')        {            onesCompliment[a] = '1';        }            }    for(int a=9; a>=0; a--)      {          if(onesCompliment[a] == '1' && b == 1)          {              twosCompliment[a] = '0';          }          else if(onesCompliment[a] == '0' && b == 1)          {              twosCompliment[a] = '1';              b = 0;          }          else          {              twosCompliment[a] = onesCompliment[a];          }      }                 printf("Two's complement of the binary number is = %s", twosCompliment);    return 0;}