//WAP to input all angles of a triangle and check whether a triangle is valid or not#include <stdio.h>int main() {    int angle1 , angle2 , angle3;    printf ("Enter all the angles of a triangle whose validity is to be checked:\n");    scanf ("%d%d%d" , &angle1,&angle2,&angle3);        if (angle1 + angle2 + angle3 == 180)    {        printf ("Triangle is valid");    }    else     {        printf ("Triangle is invalid");    }    return 0;}