//WAP to input month number and print number of days in that month#include <stdio.h>int main(){    int monthnum;        printf ("Enter the month number:\n");    scanf ("%d" , &monthnum);        if (monthnum ==4 || monthnum ==6 || monthnum ==9 || monthnum ==11 )    {        printf ("Number of days in the month are 30");    }    else if (monthnum ==2)    {        printf ("Number of days in the month are 28");    }        else     {        printf ("Number of days in the month are 31");    }    return 0;}