//WAP to swap the first and last digits of a number#include <stdio.h>#include <math.h>int main(){    int num , firstdigit, lastdigit , swapped , a , b;    printf("Enter the number whose first and last digit is to be swapped:\n");    scanf ("%d" , &num);    lastdigit = num%10;    a = num;    for (b=0 ; num >10 ; b++)    {        num = num/10;    }    firstdigit = num;        swapped = a - firstdigit * pow (10 , b) + lastdigit * pow (10,b);        swapped = swapped - lastdigit + firstdigit;        printf ("Number after swapping is %d" , swapped);    return 0;}